import React, { useEffect, useState } from "react";

export default function Home({ setAuth }) {
  const [name, setName] = useState("");

  async function getName() {
    try {
      const response = await fetch("http://localhost:5000/dashboard", {
        method: "GET",
        headers: { token: localStorage.token },
      });
      const parseRes = await response.json();

      setName(parseRes.user_name);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getName();
  }, []);

  const clearLocal = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
  };

  return (
    <div>
      <h1>Dashboard {name}</h1>
      <div className="container-fluid">
        <button
          className="btn btn-danger btn-lg float-right"
          onClick={(e) => clearLocal(e)}
        >
          Logout
        </button>
        <button
          type="button"
          className="btn btn-success btn-lg float-right mx-3"
          data-toggle="modal"
          data-target="#exampleModalLong"
        >
          Publish
        </button>
      </div>

      <div

        className="modal fade"
        id="exampleModalLong"
        tabindex="-1"
        role="dialog"
        aria-labelledby="exampleModalLongTitle"
        aria-hidden="true"
      >
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLongTitle">
                 <div className="mb-3 my-5">
                <label htmlFor="exampleFormControlTextarea1" className="form-label"><h3 ><strong>Create Your Blog</strong></h3></label>
                <textarea className="form-control" id="exampleFormControlTextarea1"   placeholder="Start typing.." style={{width:"120%"}}></textarea>
            </div>
              </h5>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">...</div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-dismiss="modal"
              >
                Close
              </button>
              <button type="button" className="btn btn-primary">
                Save changes
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
