import React, { useState } from "react";

export default function ArticleCard(props) {
  const deleteArticle = async (e) => {
    e.preventDefault();
    const article_id = e.target.name;

    const body = {
      article_id,
    };
    const response = await fetch("http://localhost:5000/home/removepost", {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        token: localStorage.token,
      },
      body: JSON.stringify(body),
    });
    const parseRes = await response.json();
    props.updateApi();
    console.log(parseRes);
  };
  const [input, setInput] = useState({

    title: "",
    content: ""
  });
  const updatePost = async (e) =>{
    e.preventDefault();
    const article_id = props.id;
    const article_title = input.title;
    const article_content = input.content;
    const body = {
      article_id,
      article_title,
      article_content
    }
    const response = await fetch("http://localhost:5000/home/edit", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        token: localStorage.token,
      },
      body: JSON.stringify(body),
    });
    const parseRes = await response.json();
    props.updateApi();
    console.log(parseRes);

  }



  const onChange = (e) =>{
    setInput({...input, [e.target.name] : e.target.value});
  }
  console.log(input);
  return (
    <div>
      <div class="container py-2">
        <div class="card">
          <div class="row ">
            <div class="col-md-12 px-3">
              <div class="card-block px-6">
                <h4 class="card-title">{props.title}</h4>
                <p class="card-text">{props.content}</p>

                <br />
                <a
                  name={props.id}
                  href="/myarticles"
                  class="mt-auto btn btn-danger shadow"
                  onClick={(e) => deleteArticle(e)}
                >
                  Delete
                </a>
                <button className="btn btn-warning  mx-2 shadow mt-auto" data-toggle="modal"
          data-target="#form" key={props.key}>
                  Edit
                </button>
                <div
                  className="modal fade"
                  id="form"
                  tabIndex="-1"
                  role="dialog"
                  aria-labelledby="exampleModalLabel"
                  aria-hidden="true"
                >
                  <div
                    className="modal-dialog modal-lg modal-dialog-centered"
                    role="document"
                  >
                    <div className="modal-content">
                      <div className="modal-header border-bottom-0">
                        <h3 className="modal-title" id="exampleModalLabel">
                          Create a blog of your own!
                        </h3>
                        <button
                          type="button"
                          className="close"
                          data-dismiss="modal"
                          aria-label="Close"
                        >
                          <span aria-hidden="true">&times;</span>
                        </button>
                      </div>
                      <form>
                        <div className="modal-body">
                          <div className="form-group">
                            <label htmlFor="email1">Blog Title</label>
                            <input
                              type="text"
                              name="title"
                              value={input.title}
                              onChange={(e) => onChange(e)}
                              className="form-control"
                              id="email1"
                              aria-describedby="emailHelp"
                              placeholder="Enter Title"
                            />
                          </div>
                          <div className="form-group">
                            <label htmlFor="email1">Blog Content</label>
                            <textarea
                              value={input.content}
                              onChange={(e) => onChange(e)}
                              name="content"
                              className="form-control"
                              id="exampleFormControlTextarea1"
                              rows="20"
                              placeholder="Start typing.."
                            ></textarea>
                          </div>
                        </div>
                        <div className="modal-footer border-top-0 d-flex justify-content-center">
                          <button
                            type="submit"
                            className="btn btn-success"
                            data-dismiss="modal"
                            name={props.id}
                            onClick={(e) => updatePost(e)}
                          >
                            Save Changes
                          </button>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
