import React, { useEffect, useState } from "react";

export default function Home({ setAuth }) {
  const [name, setName] = useState("");
  const [post, setPost] = useState({
    title: "",
    content: "",
  });

  const onChange = (e) => {
    setPost({ ...post, [e.target.name]: e.target.value });
  };

  async function getName() {
    try {
      const response = await fetch("http://localhost:5000/home", {
        method: "GET",
        headers: { token: localStorage.token },
      });
      const parseRes = await response.json();

      setName(parseRes.user_name);
    } catch (err) {
      console.error(err.message);
    }
  }

  const onSubmitForm =  (e) => {
    e.preventDefault();
    console.log("submit data to backend");
  };

  useEffect(() => {
    getName();
  }, []);

  const clearLocal = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
  };

  return (
    <div>
      <h1 style={{color:"white"}}><b>Welcome {name}</b></h1>
      <div className="container-fluid">
        <button
          className="btn btn-danger btn-lg float-right"
          onClick={(e) => clearLocal(e)}
        >
          Logout
        </button>
        <button
          type="button"
          class="btn btn-success btn-lg float-right mx-3"
          data-toggle="modal"
          data-target="#form"
        >
          Publish
        </button>
      </div>
      <div class="container"></div>

      <div
        class="modal fade"
        id="form"
        tabindex="-1"
        role="dialog"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div
          class="modal-dialog modal-lg modal-dialog-centered"
          role="document"
        >
          <div class="modal-content">
            <div class="modal-header border-bottom-0">
              <h3 class="modal-title" id="exampleModalLabel">
                Create a blog of your own!
              </h3>
              <button
                type="button"
                class="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <form>
              <div class="modal-body">
                <div class="form-group">
                  <label for="email1">Blog Title</label>
                  <input
                    type="text"
                    name="title"
                    value={post.title}
                    onChange={(e) => onChange(e)}
                    class="form-control"
                    id="email1"
                    aria-describedby="emailHelp"
                    placeholder="Enter Title"
                  />
                </div>
                <div class="form-group">
                <label for="email1">Blog Content</label>
                  <textarea
                    value={post.content}
                    onChange={(e) => onChange(e)}
                    name="content"
                    className="form-control"
                    id="exampleFormControlTextarea1"
                    rows="20"
                    placeholder="Start typing.."
                  ></textarea>
                </div>
              </div>
              <div class="modal-footer border-top-0 d-flex justify-content-center">
                <button type="submit" class="btn btn-success" data-dismiss="modal" onClick={onSubmitForm}>
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
