import React from "react";

export default function ArticleCard(props) {
  // async function deleteArticle(){
  //     const article_id = props.id;
  //     const body = {
  //         article_id
  //     }
  //     const response = await fetch("http://localhost:5000/home/removepost", {
  //         method: "DELETE",
  //         headers:{token : localStorage.token},
  //         body: JSON.stringify(body)
  //     });
  //     const parseRes = await response.json();
  //     console.log(parseRes);
  // }
  const deleteArticle = async (e) => {
    e.preventDefault();
    const article_id = e.target.name;
    console.log(article_id, "article id");
    const body = {
      article_id,
    };
    const response = await fetch("http://localhost:5000/home/removepost", {
      method: "delete",
      headers: { 
        "Content-Type": "application/json",  
        token: localStorage.token 
        },
      body: JSON.stringify(body),
    });
    const parseRes = await response.json();
    console.log(parseRes);
  };

  return (
    <div>
      <div class="container py-2">
        <div class="card">
          <div class="row ">
            <div class="col-md-12 px-3">
              <div class="card-block px-6">
                <h4 class="card-title">{props.title}</h4>
                <p class="card-text">{props.content}</p>

                <br />
                <a
                name = {props.id}
                  href="/myarticles"
                  class="mt-auto btn btn-danger"
                  onClick={(e) => deleteArticle(e)}
                >
                  Delete
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
