import React, { useEffect, useState } from 'react'
import ArticleCard from './ArticleCard'

export default function MyArticles() {

    const [myarticles, setmyarticles] = useState([]);
    async function getArticles(){
        const response = await fetch("http://localhost:5000/home/myposts", {
            method: "GET",
            headers: {token : localStorage.token}
        });

        const parseRes = await response.json();
        setmyarticles(parseRes);
        console.log(parseRes);
    }
    function updateApi(){
        getArticles();
    }
    useEffect(()=>{
        getArticles();
    }, [updateApi()]);

    return (
        <div>
            {myarticles.map((ele, key) =>{
                return <ArticleCard key={key} title={ele.article_title} content={ele.article_content} id={ele.article_id} updateApi = {() => updateApi()}/>
            })}
          
            
        </div>
    )
}
