import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function Login(props) {
  const [inputs, setInputs] = useState({
    email: "",
    password: "",
  });
  const { email, password } = inputs;

  const onChange = (e) => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { email, password };
      const response = await fetch("http://localhost:5000/auth/login", {
        method: "POST",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify(body),
      });

      const ParseRes = await response.json();
      console.log(ParseRes);
      localStorage.setItem("token", ParseRes.token);
      props.setAuth(true);
    } catch (err) {
      console.log(err.message);
    }
  };

  return (
    <div className="container">
      <form className="container-fluid d-flex flex-column my-5 shadow" style={{backgroundColor:"white", width:"80%", height: "50%", borderRadius:"0.5%"}}>
                <h3>Login</h3>

                <div className="form-group">
                    <label>User Name</label>
                    <input type="text" className="form-control" placeholder="First name" />
                </div>

                <div className="form-group">
                    <label>Email</label>
                    <input type="email" className="form-control" placeholder="Enter email" />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" />
                </div>

                <button type="submit" className="btn btn-dark btn-lg btn-block shadow">Register</button>
                <p className="forgot-password text-right">
                    Not Registered yet? <Link to="/register">Register</Link>
                </p>
            </form>
      {/* <h1 className="text-center">Login</h1>
      <form>
        <input
          type="email"
          name="email"
          placeholder="email"
          className="form-control my-3"
          value={inputs.email}
          onChange={(e) => onChange(e)}
        />
        <input
          type="password"
          name="password"
          placeholder="password"
          className="form-control my-3"
          value={inputs.password}
          onChange={(e) => onChange(e)}
        />
      </form>
      <button
        className="btn btn-success btn-block my-2"
        style={{ width: "100%" }}
        onClick={(e) => onSubmitForm(e)}
      >
        Submit
      </button> */}
    </div>
  );
}
